INSERT INTO cities (name, country, cover_image, description, location)
VALUES
('Rio de Janeiro', 'Brasil', 'digital-nomad/cover/rio-de-janeiro.jpg', 'Rio de Janeiro, a Cidade Maravilhosa, é mundialmente famosa por suas praias icônicas, montanhas exuberantes e o Carnaval. A cidade oferece uma combinação única de cultura vibrante, belezas naturais e um estilo de vida animado.', ST_GeographyFromText('POINT(-43.1729 -22.9068)')),

('Tóquio', 'Japão', 'digital-nomad/cover/tokyo.jpg', 'Tóquio, capital do Japão, combina tradição e modernidade, com templos antigos e arquitetura futurista. É um centro global de cultura, tecnologia e economia, além de ser famosa por sua gastronomia.', ST_GeographyFromText('POINT(139.6503 35.6762)')),

('Bangkok', 'Tailândia', 'digital-nomad/cover/bangkok.jpg', 'Bangkok, a capital vibrante da Tailândia, é uma cidade que mistura tradição e modernidade. Famosa por seus templos históricos, mercados de rua movimentados e arranha-céus imponentes.', ST_GeographyFromText('POINT(100.5018 13.7563)')),

('Barcelona', 'Espanha', 'digital-nomad/cover/barcelona.jpg', 'Barcelona, a vibrante capital da Catalunha, é conhecida por sua arquitetura modernista, belas praias e uma cultura rica.', ST_GeographyFromText('POINT(2.1734 41.3851)')),

('Nova York', 'Estados Unidos', 'digital-nomad/cover/new-york.jpg', 'Nova York, a cidade que nunca dorme, é um centro global de cultura, negócios e entretenimento.', ST_GeographyFromText('POINT(-74.006 40.7128)')),

('Krabi', 'Tailândia', 'digital-nomad/cover/krabi.jpg', 'Krabi, localizada na costa oeste da Tailândia, é famosa por suas paisagens deslumbrantes, praias de areia branca e águas cristalinas.', ST_GeographyFromText('POINT(98.9063 8.0863)')),

('Bali', 'Indonésia', 'digital-nomad/cover/bali.jpg', 'Bali, também conhecida como a Ilha dos Deuses, é um dos destinos turísticos mais procurados do mundo.', ST_GeographyFromText('POINT(115.092 -8.3405)')),

('Amsterdã', 'Países Baixos', 'digital-nomad/cover/amsterdam.jpg', 'Amsterdã, a capital vibrante dos Países Baixos, é conhecida por seus canais pitorescos, museus renomados e uma rica história cultural.', ST_GeographyFromText('POINT(4.9041 52.3676)')),

('Cancún', 'México', 'digital-nomad/cover/cancun.jpg', 'Cancún, localizada na costa caribenha do México, é famosa por suas praias de areia branca, águas cristalinas e vida noturna vibrante.', ST_GeographyFromText('POINT(-86.8515 21.1619)')),

('Dubai', 'Emirados Árabes Unidos', 'digital-nomad/cover/dubai.jpg', 'Dubai, uma cidade global conhecida por seu luxo, inovação e arquitetura impressionante.', ST_GeographyFromText('POINT(55.296249 25.276987)')),

('Cidade do México', 'México', 'digital-nomad/cover/ciudad-de-mexico.jpg', 'A Cidade do México é uma metrópole vibrante que combina história, cultura e modernidade.', ST_GeographyFromText('POINT(-99.1332 19.4326)')),

('Hong Kong', 'China', 'digital-nomad/cover/hong-kong.jpg', 'Hong Kong, uma das cidades mais vibrantes e densamente povoadas do mundo, conhecida por seu horizonte icônico.', ST_GeographyFromText('POINT(114.1694 22.3193)')),

('Košice', 'Eslováquia', 'digital-nomad/cover/kosice.jpg', 'Košice, a segunda maior cidade da Eslováquia, é um centro histórico e cultural com uma rica arquitetura gótica.', ST_GeographyFromText('POINT(21.2611 48.7164)')),

('Melbourne', 'Austrália', 'digital-nomad/cover/melbourne.jpg', 'Melbourne, a capital cultural da Austrália, é conhecida por sua vibrante cena artística, vida noturna e cafés.', ST_GeographyFromText('POINT(144.9631 -37.8136)')),

('Singapura', 'Singapura', 'digital-nomad/cover/singapore.jpg', 'Singapura, uma cidade-estado moderna e cosmopolita, é conhecida por seu impressionante horizonte urbano e jardins futuristas.', ST_GeographyFromText('POINT(103.8198 1.3521)'));
